        /* Definimos la paleta de colores */
:root {
    --primary-color: #2c3e50; /* Azul oscuro */
    --secondary-color: #d0d3e0; /* Blanco humo */
    --accent-color: #e74c3c; /* Rojo coral */
    --background-color: #f9f9f9; /* Gris claro */
    --button-color: #3498db; /* Azul claro */
    --hover-color: #2980b9; /* Azul más oscuro para hover */
    --text-color: #2c3e50; /* Color de texto primario */
    --border-color: #95a5a6; /* Gris suave para bordes */
    --modal-background-color: rgba(44, 62, 80, 0.6); /* Azul oscuro semi-transparente para modales */
}

/* Estilos CSS */
body {
    font-family: 'Roboto', sans-serif;
    background-color: var(--background-color);
    margin: 0;
    padding: 0;
}

.container {
    max-width: 900px;
    margin: 50px auto;
    padding: 30px;
    background-color: var(--secondary-color);
    border-radius: 20px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
}

.vehicle {
    display: inline-block;
    margin: 20px;
    padding: 20px;
    border: 2px solid var(--accent-color);
    border-radius: 10px;
    text-align: center;
    transition: transform 0.3s ease-in-out;
}

.vehicle:hover {
    transform: scale(1.1);
}

.vehicle img {
    max-width: 250px;
    max-height: 180px;
    border-radius: 10px;
}

.category-button {
    margin: 10px;
    padding: 10px 20px;
    font-size: 18px;
    background-color: var(--button-color);
    color: var(--secondary-color);
    border: none;
    border-radius: 5px;
    cursor: pointer;
}

.category-button:hover {
    background-color: var(--hover-color);
}

/* Nuevos estilos para botones de navegación */
.nav-button {
    margin: 10px;
    padding: 10px 20px;
    font-size: 18px;
    background-color: var(--nav-button-color);
    color: var(--nav-text-color);
    border: none;
    border-radius: 5px;
    cursor: pointer;
}

.nav-button:hover {
    background-color: var(--nav-hover-color);
}

.contact-info {
    margin-top: 40px;
    background-color: var(--secondary-color);
    padding: 25px;
    border-radius: 15px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
}

.contact-info h2 {
    margin-bottom: 20px;
    text-align: center;
    color: var(--text-color);
}

.contact-info ul {
    list-style-type: none;
    padding: 0;
}

.contact-info ul li {
    margin-bottom: 10px;
    font-size: 18px;
}

.contact-info ul li strong {
    font-weight: bold;
    color: var(--text-color);
}

.contact-info p {
    text-align: center;
    font-size: 16px;
    color: var(--border-color);
}

/* Estilos para la ventana modal */
.modal {
    display: none;
    position: fixed;
    z-index: 1;
    padding-top: 80px;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: var(--modal-background-color);
}

.modal-content {
    background-color: var(--secondary-color);
    margin: 10% auto;
    padding: 30px;
    border: none;
    width: 70%;
    max-width: 500px;
    border-radius: 15px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
}

.close {
    color: var(--border-color);
    float: right;
    font-size: 30px;
    font-weight: bold;
}

.close:hover,
.close:focus {
    color: var(--text-color);
    text-decoration: none;
    cursor: pointer;
}



