<script>
    function filterVehicles(category) {
        var vehicles = document.getElementsByClassName('vehicle');
        for (var i = 0; i < vehicles.length; i++) {
            if (category === 'todos' || vehicles[i].getAttribute('data-category') === category) {
                vehicles[i].style.display = 'block';
            } else {
                vehicles[i].style.display = 'none';
            }
        }
    }

    function showDetails(title, description, price) {
        document.getElementById('modalTitle').innerText = title;
        document.getElementById('modalDescription').innerText = description;
        document.getElementById('modalPrice').innerText = price;
        document.getElementById('detailsModal').style.display = 'block';
    }

    function closeDetails() {
        document.getElementById('detailsModal').style.display = 'none';
    }
</script>
